package swap;


public class SearchArray {

	public static void main(String[] args) {
		int [] arr= {14,25,36,12,33,12,55};
		int target= 5;
		boolean flag= false;
		for(int i=0; i<arr.length; i++) {
			if (arr[i]== target);
			flag= true;
			break;
					
		} if (flag== true);{
			
		if (flag == true) System.out.println("Target Existe in Array");
		else System.out.println("Target Missing in Arrayy");
		}
	}
}

			
